function EventManager(){
	this.callback_list = {};
	this.load_room_id_req = 'load_room_id_req';
    this.load_room_id_res = 'load_room_id_res';
    this.create_room_req = 'create_room_req';
    this.create_room_res = 'create_room_res';
    this.join_room_req = 'join_room_req';
    this.join_room_res = 'join_room_res';
    this.save_canvas = 'save_canvas';
	
}

EventManager.prototype.on = function(event , callback){
	if(!this[event]) {console.log(event +' not exist'); return false;}
	if(this.callback_list[event]) {console.log('event has been on'); return false;}
	this.callback_list[event] = callback;
};

EventManager.prototype.emit = function(event , data , socket){
	var e;
	var callback_list = this.callback_list;
	for(e in callback_list){
		if(e === event){
			var callback = callback_list[e];
			callback(data , socket);
			break;
		}else{
			//do nothing
		}
	}
};



module.exports = EventManager;